import { Image, StyleSheet, Text, View } from "react-native";
import { MEALS } from "../data/dummry-data";
import MealDetails from "./mealDetails";

const MaalDetailsScreen = ({ route, navigation }) => {
  const mealId = route.params.mealId;
  const seletedMeal = MEALS.find((item) => item.id === mealId);
  return (
    <View>
      <Image style={style.image} source={{ uri: seletedMeal.imageUrl }} />
      <Text style={style.title}>{seletedMeal.title}</Text>
      <MealDetails
        duration={seletedMeal.duration}
        complexity={seletedMeal.complexity}
        affordability={seletedMeal.affordability}
        textStyle={style.detailsText}
      />
      <View style={style.subtitleContainer}>  
      <Text style={style.subtitle}>Ingredients</Text>
      </View>

      {seletedMeal.ingredients.map((ingredient) => (
        <Text key={ingredient}>{ingredient}</Text>
      ))}
      <View style={style.subtitleContainer}>  
      <Text style={style.subtitle}>steps</Text>

      </View>
      {seletedMeal.steps.map((step) => (
        <Text key={step}>{step}</Text>
      ))}
    </View>
  );
};
export default MaalDetailsScreen;
const style=StyleSheet.create({
    image:{
        width:'100%',
        height:350
    },
    title:{
        fontWeight:'bold',
        fontSize:24,
        margin:8,
        textAlign:'center',
        color:'white'
    },
    detailsText:{
        color:'white'
    },
    subtitle:{
        color:'#e2b497',
        fontSize:18,
        fontWeight:'bold',
        textAlign:'center',

    },
    subtitleContainer:{
        padding:6,
        margin:4,
        borderBottomColor:'#e2b497',
        borderBottomWidth:2,
        marginHorizontal:24,
        marginVertical:4
    }
})
