import { useLayoutEffect } from "react";
import { FlatList, StyleSheet, Text, View } from "react-native";
import MealsItem from "../component/mealItem";
import { CATEGORIES, MEALS } from "../data/dummry-data";

const MealsOverviewScreen = ({ route ,navigation}) => {
  const catID=route.params.categoryId
  const renderMealsItem = MEALS.filter(
    (item) => item.categoryIds.indexOf(route.params.categoryId) >= 0
  );

  const handelMealsOverView = (itemData) => {

    const item=itemData.item;
    const mealItemProps={
        title:item.title,
        imageUrl:item.imageUrl,
        affordability:item.affordability,
        complexity:item.complexity,
        duration:item.duration
    }
    return <MealsItem  {...mealItemProps}/>;
  };

  useLayoutEffect(()=>{
    navigation.setOptions({
      title:CATEGORIES.find((category)=>category.id === route.params.categoryId).title
    })
  },[navigation,catID])


  return (
    <View style={style.rootContainer}>
      {/* <Text>Meals OVerview screens-{route.params.categoryId}</Text> */}
      <FlatList
        data={renderMealsItem}
        keyExtractor={(item) => item.id}
        renderItem={handelMealsOverView}
      />
    </View>
  );
};
export default MealsOverviewScreen;

const style = StyleSheet.create({
  rootContainer: {
    flex: 1,
    padding: 20,
  },
});
